let grouped = {};
let details = {};

for (let item of items) {
    let noticeID = item.json["Site_Notice_ID"];
    noticeID = noticeID.replace(/\s/g, '');  // Remove all white spaces

    const noticeDetail = item.json["Notice_ID"];
    const projectId = item.json["Project_ID"];
    const issuedTo = item.json["Issued_To"];
    const issueToEmail = item.json["Issue_To_Email"];
    const created = item.json["Created"];
    const createdBy = item.json["CreatedBy"];
    const openDays = item.json["Open_Days"];
    const status = item.json["Status"];
    const signOffStatus = item.json["Sign_Off_Status"];
    const listId = item.json["List_ID"];
    let ccRecipients = item.json["ccRecipients"];

    // Only attempt to remove spaces if ccRecipients is not null or undefined
    if (ccRecipients) {
        ccRecipients = ccRecipients.replace(/\s/g, '');
    }

    const projectName = item.json["Project_Name"];
    const createdPersonEmail = item.json["Created_Person_Email"];

    if (grouped[noticeID]) {
        grouped[noticeID].push(noticeDetail);
    } else {
        grouped[noticeID] = [noticeDetail];
        details[noticeID] = {projectId, issuedTo, issueToEmail, created, createdBy, openDays, status, signOffStatus, listId, ccRecipients, projectName, createdPersonEmail};
    }
}

return Object.keys(grouped).map(noticeID => {
    const links = grouped[noticeID].map(id => 
      `<a href="https://cairnmead.org/site-notice?nid=${id}&snid=${noticeID}&pid=${details[noticeID].projectId}&list=${details[noticeID].listId}">Notice Item: ${id}</a>`
    );

    return {
        json: {
            "Site_Notice_ID": noticeID,
            "Notice_Links": links.join('<br>'),
            ...details[noticeID]
        }
    };
});

